const slidesData = [{"content":"<div class=\"slide title-slide\">\n  <div class=\"logo-container\">\n    <img src=\"assets/_presentation_project_ceres-tech-logo.webp\" alt=\"Ceres AI Logo\" class=\"title-logo\">\n  </div>\n  <h1 class=\"main-title\">Stop Fighting Wraparound</h1>\n  <h2 class=\"subtitle\">Embed Your Rings!</h2>\n\n  <div class=\"visual-element\">\n    <svg width=\"400\" height=\"200\" viewBox=\"0 0 400 200\">\n      <!-- Circle representing wraparound -->\n      <circle\n        cx=\"100\"\n        cy=\"100\"\n        r=\"80\"\n        fill=\"none\"\n        stroke=\"#4CAF50\"\n        stroke-width=\"3\"\n      />\n\n      <!-- Points near wraparound -->\n      <circle cx=\"170\" cy=\"100\" r=\"8\" fill=\"#FF5722\" />\n      <circle cx=\"30\" cy=\"100\" r=\"8\" fill=\"#2196F3\" />\n\n      <!-- Arrow showing \"wrong\" linear distance -->\n      <path\n        d=\"M 170 100 L 30 100\"\n        stroke=\"#FF5722\"\n        stroke-width=\"2\"\n        stroke-dasharray=\"5,5\"\n        opacity=\"0.5\"\n      />\n      <text x=\"100\" y=\"90\" text-anchor=\"middle\" fill=\"#FF5722\" font-size=\"14\">\n        140Â° apart?\n      </text>\n\n      <!-- Arrow showing correct arc distance -->\n      <path\n        d=\"M 170 100 A 80 80 0 0 0 30 100\"\n        fill=\"none\"\n        stroke=\"#4CAF50\"\n        stroke-width=\"3\"\n      />\n      <text\n        x=\"100\"\n        y=\"130\"\n        text-anchor=\"middle\"\n        fill=\"#4CAF50\"\n        font-size=\"14\"\n        fill=\"#FFC107\"\n      >\n        20Â° apart!\n      </text>\n\n      <!-- Embedding arrow -->\n      <path\n        d=\"M 220 100 L 280 100\"\n        stroke=\"#666\"\n        stroke-width=\"2\"\n        marker-end=\"url(#arrowhead)\"\n      />\n      <text\n        x=\"250\"\n        y=\"90\"\n        text-anchor=\"middle\"\n        fill=\"#666\"\n        font-size=\"12\"\n        fill=\"#FFC107\"\n      >\n        embed\n      </text>\n\n      <!-- 2D representation -->\n      <g transform=\"translate(320, 100)\">\n        <line x1=\"-50\" y1=\"0\" x2=\"50\" y2=\"0\" stroke=\"#999\" stroke-width=\"1\" />\n        <line x1=\"0\" y1=\"-50\" x2=\"0\" y2=\"50\" stroke=\"#999\" stroke-width=\"1\" />\n        <circle cx=\"40\" cy=\"0\" r=\"8\" fill=\"#FF5722\" />\n        <circle cx=\"-40\" cy=\"0\" r=\"8\" fill=\"#2196F3\" />\n        <text\n          x=\"0\"\n          y=\"-60\"\n          text-anchor=\"middle\"\n          fill=\"#666\"\n          font-size=\"12\"\n          fill=\"#FFC107\"\n        >\n          â„Â²\n        </text>\n      </g>\n\n      <!-- Arrow marker -->\n      <defs>\n        <marker\n          id=\"arrowhead\"\n          markerWidth=\"10\"\n          markerHeight=\"7\"\n          refX=\"10\"\n          refY=\"3.5\"\n          orient=\"auto\"\n        >\n          <polygon points=\"0 0, 10 3.5, 0 7\" fill=\"#666\" />\n        </marker>\n      </defs>\n    </svg>\n  </div>\n\n  <div class=\"presenter-info\">\n    <p>A practical guide to handling circular quantities in code</p>\n    <p class=\"author\">Ceres</p>\n    <p class=\"date\">Tech Talk</p>\n  </div>\n</div>\n","title":"Stop Fighting Wraparound"},{"content":"<div class=\"slide\">\n    <h1>The Bug We've All Written</h1>\n    \n    <div class=\"highlight-box\">\n        <h3>âŒ The Naive Approach</h3>\n        <pre><code class=\"language-python\">def average_angle(angle1, angle2):\n    return (angle1 + angle2) / 2  # Seems reasonable...\n\n# Example: Average heading between two compass readings\nheading1 = 350  # degrees\nheading2 = 10   # degrees\n\naverage = average_angle(heading1, heading2)\nprint(f\"Average: {average}Â°\")  # 180Â° - COMPLETELY WRONG!</code></pre>\n    </div>\n    \n    <div class=\"visual-demo\">\n        <svg width=\"600\" height=\"600\" viewBox=\"0 0 300 300\">\n            <!-- Compass circle -->\n            <circle cx=\"150\" cy=\"150\" r=\"120\" fill=\"none\" stroke=\"#333\" stroke-width=\"2\"/>\n            \n            <!-- Compass labels -->\n            <text x=\"150\" y=\"20\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">N (0Â°)</text>\n            <text x=\"280\" y=\"155\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">E (90Â°)</text>\n            <text x=\"150\" y=\"290\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">S (180Â°)</text>\n            <text x=\"20\" y=\"155\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">W (270Â°)</text>\n            \n            <!-- Heading 1: 350Â° -->\n            <line x1=\"150\" y1=\"150\" x2=\"140\" y2=\"30\" stroke=\"#2196F3\" stroke-width=\"4\"/>\n            <circle cx=\"140\" cy=\"30\" r=\"8\" fill=\"#2196F3\"/>\n            <text x=\"110\" y=\"20\" font-size=\"14\" fill=\"#FFC107\">350Â°</text>\n            \n            <!-- Heading 2: 10Â° -->\n            <line x1=\"150\" y1=\"150\" x2=\"160\" y2=\"30\" stroke=\"#4CAF50\" stroke-width=\"4\"/>\n            <circle cx=\"160\" cy=\"30\" r=\"8\" fill=\"#4CAF50\"/>\n            <text x=\"190\" y=\"20\" font-size=\"14\" fill=\"#FFC107\">10Â°</text>\n            \n            <!-- Wrong average: 180Â° -->\n            <line x1=\"150\" y1=\"150\" x2=\"150\" y2=\"270\" stroke=\"#FF5722\" stroke-width=\"4\" stroke-dasharray=\"5,5\"/>\n            <circle cx=\"150\" cy=\"270\" r=\"8\" fill=\"#FF5722\"/>\n            <text x=\"150\" y=\"260\" text-anchor=\"middle\" font-size=\"16\" fill=\"#FF5722\" font-weight=\"bold\">\n                Wrong: 180Â°\n            </text>\n            \n            <!-- Correct average indicator -->\n            <line x1=\"150\" y1=\"150\" x2=\"150\" y2=\"30\" stroke=\"#FFC107\" stroke-width=\"3\" opacity=\"0.7\"/>\n            <text x=\"220\" y=\"60\" font-size=\"14\" fill=\"#ffffff\" font-weight=\"bold\">Should be ~0Â°!</text>\n        </svg>\n    </div>\n    \n    <div class=\"warning-box\">\n        <h3>What Went Wrong?</h3>\n        <ul>\n            <li>350Â° and 10Â° are <strong>20Â° apart</strong> on the circle</li>\n            <li>But linear math sees them as <strong>340Â° apart</strong></li>\n            <li>The average points in the <strong>opposite direction</strong>!</li>\n        </ul>\n    </div>\n</div>\n\n","title":"The Bug We've All Written"},{"content":"<div class=\"slide\">\n    <h1>The Midnight Bug</h1>\n    \n    <div class=\"info-box\">\n        <h3>ğŸ“… Real Scenario: Event Duration Tracking</h3>\n        <p>Meeting started at <strong>23:45</strong> (11:45 PM)</p>\n        <p>Meeting ended at <strong>00:15</strong> (12:15 AM)</p>\n    </div>\n    \n    <div class=\"code-comparison\">\n        <div class=\"highlight-box\">\n            <h4>âŒ What Everyone Tries First</h4>\n            <pre><code>start_time = 23.75  # 23:45 in decimal hours\nend_time = 0.25     # 00:15 in decimal hours\n\nduration = end_time - start_time\nprint(f\"Duration: {duration} hours\")\n# Output: -23.5 hours ğŸ¤¦</code></pre>\n        </div>\n        \n        <div class=\"warning-box\">\n            <h4>ğŸ¤” The Hacky \"Fix\"</h4>\n            <pre><code>if end_time < start_time:\n    # Must have crossed midnight\n    duration = (24 - start_time) + end_time\nelse:\n    duration = end_time - start_time\n    \n# More special cases...\nif crossed_dst:  # Daylight savings\n    duration += 1  # or -1? ğŸ˜­</code></pre>\n        </div>\n    </div>\n    \n    <div class=\"clock-visual\">\n        <svg width=\"1200\" height=\"500\" viewBox=\"0 0 800 300\">\n            <!-- Linear representation -->\n            <g transform=\"translate(30, 80)\">\n                <text x=\"150\" y=\"-20\" text-anchor=\"middle\" font-size=\"14\" font-weight=\"bold\" fill=\"#FFC107\">Linear View:</text>\n                <line x1=\"0\" y1=\"20\" x2=\"300\" y2=\"20\" stroke=\"#333\" stroke-width=\"2\"/>\n                \n                <!-- Time markers -->\n                <line x1=\"0\" y1=\"15\" x2=\"0\" y2=\"25\" stroke=\"#333\" stroke-width=\"2\"/>\n                <text x=\"0\" y=\"40\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">0:00</text>\n                \n                <line x1=\"150\" y1=\"15\" x2=\"150\" y2=\"25\" stroke=\"#333\" stroke-width=\"2\"/>\n                <text x=\"150\" y=\"40\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">12:00</text>\n                \n                <line x1=\"300\" y1=\"15\" x2=\"300\" y2=\"25\" stroke=\"#333\" stroke-width=\"2\"/>\n                <text x=\"300\" y=\"40\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">24:00</text>\n                \n                <!-- Start time: 23:45 (23.75/24 * 300 = 296.25) -->\n                <circle cx=\"296\" cy=\"20\" r=\"8\" fill=\"#2196F3\"/>\n                <text x=\"296\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">23:45</text>\n                \n                <!-- End time: 00:15 (0.25/24 * 300 = 3.125) -->\n                <circle cx=\"3\" cy=\"20\" r=\"8\" fill=\"#4CAF50\"/>\n                <text x=\"3\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">00:15</text>\n                \n                <!-- Wrong distance -->\n                <path d=\"M 296 20 L 3 20\" stroke=\"#FF5722\" stroke-width=\"2\" stroke-dasharray=\"5,5\" opacity=\"0.5\"/>\n                <text x=\"150\" y=\"80\" text-anchor=\"middle\" fill=\"#FF5722\" font-size=\"14\">Linear: -23.5 hours!</text>\n            </g>\n            \n            <!-- Circular representation -->\n            <g transform=\"translate(550, 150)\">\n                <text x=\"0\" y=\"-90\" text-anchor=\"middle\" font-size=\"14\" font-weight=\"bold\" fill=\"#FFC107\">Circular View:</text>\n                <circle cx=\"0\" cy=\"0\" r=\"70\" fill=\"none\" stroke=\"#333\" stroke-width=\"2\"/>\n                \n                <!-- Clock numbers -->\n                <text x=\"0\" y=\"-55\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">12</text>\n                <text x=\"55\" y=\"5\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">3</text>\n                <text x=\"0\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">6</text>\n                <text x=\"-55\" y=\"5\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">9</text>\n                \n                <!-- Start: 23:45 -->\n                <line x1=\"0\" y1=\"0\" x2=\"-10\" y2=\"-68\" stroke=\"#2196F3\" stroke-width=\"3\"/>\n                <circle cx=\"-10\" cy=\"-68\" r=\"8\" fill=\"#2196F3\"/>\n                \n                <!-- End: 00:15 -->\n                <line x1=\"0\" y1=\"0\" x2=\"10\" y2=\"-68\" stroke=\"#4CAF50\" stroke-width=\"3\"/>\n                <circle cx=\"10\" cy=\"-68\" r=\"8\" fill=\"#4CAF50\"/>\n                \n                <!-- Correct arc -->\n                <path d=\"M -10 -68 A 70 70 0 0 1 10 -68\" fill=\"none\" stroke=\"#4CAF50\" stroke-width=\"3\"/>\n                <text x=\"0\" y=\"90\" text-anchor=\"middle\" fill=\"#4CAF50\" font-size=\"14\" font-weight=\"bold\">\n                    Correct: 30 minutes!\n                </text>\n            </g>\n        </svg>\n    </div>\n    \n    <div class=\"success-box\">\n        <h3>ğŸ’¡ The Key Insight</h3>\n        <p>Time-of-day is a <strong>24-hour ring</strong>, not a line!</p>\n        <p>Linear arithmetic breaks at the wraparound point.</p>\n    </div>\n</div>\n\n","title":"The Midnight Bug"},{"content":"<div class=\"slide\">\n    <h1>The Missing Math Class: Circular Operations</h1>\n    \n    <div class=\"warning-box\">\n        <h3>ğŸ¤¯ What They Never Taught You</h3>\n        <p>Circular quantities are everywhere, but traditional math classes skip the operations entirely! Here's what you can actually <strong>do</strong> once you embed them as vectors:</p>\n    </div>\n    \n    <div class=\"two-column-equal\">\n        <div class=\"card\">\n            <h3>ğŸ“Š Basic Operations</h3>\n            <div class=\"operation-list\">\n                <div class=\"op-item\">\n                    <strong>Addition:</strong> <code>vâ‚ + vâ‚‚</code><br>\n                    <small>Compose two rotations</small>\n                </div>\n                <div class=\"op-item\">\n                    <strong>Subtraction:</strong> <code>vâ‚ - vâ‚‚</code><br>\n                    <small>Find angular difference</small>\n                </div>\n                <div class=\"op-item\">\n                    <strong>Average:</strong> <code>mean(vectors)</code><br>\n                    <small>Circular mean (always works!)</small>\n                </div>\n                <div class=\"op-item\">\n                    <strong>Interpolation:</strong> <code>slerp(vâ‚, vâ‚‚, t)</code><br>\n                    <small>Smooth rotation between angles</small>\n                </div>\n            </div>\n        </div>\n        \n        <div class=\"card\">\n            <h3>ğŸ§® Advanced Operations</h3>\n            <div class=\"operation-list\">\n                <div class=\"op-item\">\n                    <strong>Distance:</strong> <code>shortest_path(Î¸â‚, Î¸â‚‚)</code><br>\n                    <small>Minimal angular separation</small>\n                </div>\n                <div class=\"op-item\">\n                    <strong>Scaling:</strong> <code>scalar Ã— vector</code><br>\n                    <small>Scale magnitude, preserve direction</small>\n                </div>\n                <div class=\"op-item\">\n                    <strong>Derivatives:</strong> <code>d/dt [cos(Ï‰t), sin(Ï‰t)]</code><br>\n                    <small>Angular velocity vectors</small>\n                </div>\n                <div class=\"op-item\">\n                    <strong>Integration:</strong> <code>âˆ« Ï‰(t) dt</code><br>\n                    <small>Accumulate rotations over time</small>\n                </div>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"success-box\">\n        <h3>ğŸ“ˆ Circular Statistics (The Really Cool Stuff)</h3>\n        <div class=\"code-example\">\n            <pre><code class=\"language-python\"># Operations that actually work on circular data!\nangles = [350Â°, 10Â°, 5Â°, 355Â°]  # Wind directions\nvectors = [to_vector(Î¸) for Î¸ in angles]\n\n# Circular mean (not 180Â°!)\nmean_vector = np.mean(vectors, axis=0) \nmean_angle = from_vector(mean_vector)  # â‰ˆ 0Â°\n\n# Circular standard deviation  \ndispersion = 1 - |mean_vector|  # How \"spread out\" are they?\n\n# Circular correlation\ncorrelation = correlate_circular(wind_dir, wave_dir)\n\n# Circular regression (yes, this exists!)\nslope, intercept = circular_regression(time, wave_directions)</code></pre>\n        </div>\n    </div>\n    \n    <div class=\"info-box\">\n        <h3>ğŸ¯ Why This Matters</h3>\n        <div class=\"three-column\">\n            <div>\n                <h4>ğŸŒŠ Oceanography</h4>\n                <p>Wave directions, current flows</p>\n            </div>\n            <div>\n                <h4>ğŸ§­ Navigation</h4>\n                <p>GPS, compass bearings, flight paths</p>\n            </div>\n            <div>\n                <h4>ğŸ§¬ Biology</h4>\n                <p>Circadian rhythms, animal migration</p>\n            </div>\n            <div>\n                <h4>ğŸµ Signal Processing</h4>\n                <p>Phase analysis, Fourier transforms</p>\n            </div>\n            <div>\n                <h4>ğŸ¤– Robotics</h4>\n                <p>Joint angles, orientation control</p>\n            </div>\n            <div>\n                <h4>ğŸ“Š Data Science</h4>\n                <p>Seasonal trends, cyclic patterns</p>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"highlight-box\">\n        <h3>ğŸ’¡ The Big Insight</h3>\n        <p><strong>Every operation you know from linear algebra works on circular quantitiesâ€”you just need to embed them first!</strong> This is why embedding is such a powerful mathematical technique.</p>\n        <div class=\"formula-showcase\">\n            <code>Circular Quantity â†’ Vector â†’ Linear Operation â†’ Vector â†’ Circular Result</code>\n        </div>\n    </div>\n</div>","title":"The Missing Math Class: Circular Operations"},{"content":"<div class=\"slide\">\n    <h1>Circular Statistics: The Hidden Superpower</h1>\n    \n    <div class=\"warning-box\">\n        <h3>ğŸ¤¯ What Statistics Class Never Taught You</h3>\n        <p>Standard statistics breaks horribly on circular data. But vector embedding makes circular statistics <strong>trivially easy</strong>â€”and incredibly powerful!</p>\n    </div>\n    \n    <div class=\"two-column-equal\">\n        <div class=\"card\">\n            <h3>ğŸ“Š The Problem with Regular Stats</h3>\n            <div class=\"code-example\">\n                <pre><code class=\"language-python\"># Wind directions (degrees)\nangles = [350, 10, 5, 355]\n\n# Regular mean = DISASTER\nmean = sum(angles) / len(angles)  # = 180Â°\n# 180Â° is the OPPOSITE direction! ğŸ¤¦â€â™‚ï¸\n\n# Regular standard deviation = MEANINGLESS\nstd = numpy.std(angles)  # = 187Â°\n# Standard deviation larger than the range!</code></pre>\n            </div>\n        </div>\n        \n        <div class=\"card\">\n            <h3>âœ¨ Vector Statistics = Magic</h3>\n            <div class=\"code-example\">\n                <pre><code class=\"language-python\"># Convert to vectors\nvectors = [angle_to_vector(Î¸) for Î¸ in angles]\n\n# Circular mean = WORKS!\nmean_vector = np.mean(vectors, axis=0)\nmean_angle = vector_to_angle(mean_vector)  # â‰ˆ 0Â°\n# Correct direction! ğŸ¯\n\n# Circular standard deviation\nR = |mean_vector|  # Mean vector length\ncircular_std = sqrt(-2 * log(R))  # â‰ˆ 22Â°\n# Sensible measure of spread!</code></pre>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"info-box\">\n        <h3>ğŸ§  The Key Insight: Mean Vector Length</h3>\n        <div class=\"two-column\">\n            <div class=\"visual-element\">\n                <svg width=\"300\" height=\"200\" viewBox=\"0 0 300 200\">\n                    <!-- Tight cluster -->\n                    <g transform=\"translate(75, 100)\">\n                        <circle r=\"50\" fill=\"none\" stroke=\"#666\" stroke-width=\"1\" opacity=\"0.3\"/>\n                        <!-- Data points clustered tightly -->\n                        <circle cx=\"45\" cy=\"5\" r=\"4\" fill=\"#4CAF50\"/>\n                        <circle cx=\"47\" cy=\"-3\" r=\"4\" fill=\"#4CAF50\"/>\n                        <circle cx=\"43\" cy=\"3\" r=\"4\" fill=\"#4CAF50\"/>\n                        <circle cx=\"46\" cy=\"0\" r=\"4\" fill=\"#4CAF50\"/>\n                        <!-- Strong mean vector -->\n                        <path d=\"M 0 0 L 40 0\" stroke=\"#FF5722\" stroke-width=\"4\" marker-end=\"url(#arrow)\"/>\n                        <text x=\"0\" y=\"-65\" text-anchor=\"middle\" fill=\"#4CAF50\" font-size=\"12\">Tight Cluster</text>\n                        <text x=\"0\" y=\"75\" text-anchor=\"middle\" fill=\"#FF5722\" font-size=\"11\">|R| â‰ˆ 0.95</text>\n                        <text x=\"0\" y=\"87\" text-anchor=\"middle\" fill=\"#666\" font-size=\"10\">Low variance</text>\n                    </g>\n                    \n                    <!-- Spread out data -->\n                    <g transform=\"translate(225, 100)\">\n                        <circle r=\"50\" fill=\"none\" stroke=\"#666\" stroke-width=\"1\" opacity=\"0.3\"/>\n                        <!-- Data points spread out -->\n                        <circle cx=\"45\" cy=\"5\" r=\"4\" fill=\"#2196F3\"/>\n                        <circle cx=\"-30\" cy=\"35\" r=\"4\" fill=\"#2196F3\"/>\n                        <circle cx=\"-15\" cy=\"-45\" r=\"4\" fill=\"#2196F3\"/>\n                        <circle cx=\"20\" cy=\"-40\" r=\"4\" fill=\"#2196F3\"/>\n                        <!-- Weak mean vector -->\n                        <path d=\"M 0 0 L 8 -5\" stroke=\"#FF5722\" stroke-width=\"4\" marker-end=\"url(#arrow)\"/>\n                        <text x=\"0\" y=\"-65\" text-anchor=\"middle\" fill=\"#2196F3\" font-size=\"12\">Spread Out</text>\n                        <text x=\"0\" y=\"75\" text-anchor=\"middle\" fill=\"#FF5722\" font-size=\"11\">|R| â‰ˆ 0.1</text>\n                        <text x=\"0\" y=\"87\" text-anchor=\"middle\" fill=\"#666\" font-size=\"10\">High variance</text>\n                    </g>\n                    \n                    <defs>\n                        <marker id=\"arrow\" markerWidth=\"10\" markerHeight=\"7\" refX=\"10\" refY=\"3.5\" orient=\"auto\">\n                            <polygon points=\"0 0, 10 3.5, 0 7\" fill=\"#FF5722\"/>\n                        </marker>\n                    </defs>\n                </svg>\n            </div>\n            <div>\n                <h4>ğŸ’¡ The Beautiful Truth</h4>\n                <div class=\"formula-showcase\">\n                    <p><strong>Tight cluster:</strong> Mean vector is long (|R| â†’ 1)</p>\n                    <p><strong>Spread out:</strong> Mean vector is short (|R| â†’ 0)</p>\n                    <p><strong>Uniform circle:</strong> Mean vector â‰ˆ 0</p>\n                </div>\n                <p>The length of the mean vector automatically captures how \"concentrated\" your circular data is!</p>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"success-box\">\n        <h3>ğŸ”¬ Advanced Circular Stats That Actually Work</h3>\n        <div class=\"code-example\">\n            <pre><code class=\"language-python\"># Circular correlation between two angular variables\ndef circular_correlation(angles1, angles2):\n    v1 = [angle_to_vector(Î¸) for Î¸ in angles1]\n    v2 = [angle_to_vector(Î¸) for Î¸ in angles2]\n    # Standard correlation on the vector components\n    return pearsonr(v1, v2)\n\n# Circular regression (yes, this exists!)\ndef circular_regression(x, circular_y):\n    vectors_y = [angle_to_vector(Î¸) for Î¸ in circular_y]\n    # Regress each component separately\n    cos_model = LinearRegression().fit(x, [v[0] for v in vectors_y])\n    sin_model = LinearRegression().fit(x, [v[1] for v in vectors_y])\n    return cos_model, sin_model\n\n# Watson-Williams test (circular ANOVA)\n# Test if multiple groups have the same mean direction\ndef watson_williams_test(group1_angles, group2_angles):\n    # Convert to vectors, compute mean directions, test significance\n    # (This is the circular equivalent of a t-test!)</code></pre>\n        </div>\n    </div>\n    \n    <div class=\"highlight-box\">\n        <h3>ğŸŒŸ Real-World Applications</h3>\n        <div class=\"three-column\">\n            <div>\n                <h4>ğŸ§¬ Biology</h4>\n                <p><strong>Animal migration:</strong> Test if birds change direction by season</p>\n                <p><strong>Circadian rhythms:</strong> Correlate activity with time of day</p>\n            </div>\n            <div>\n                <h4>ğŸŒŠ Earth Sciences</h4>\n                <p><strong>Wind patterns:</strong> Seasonal wind direction changes</p>\n                <p><strong>Ocean currents:</strong> Correlation with tidal cycles</p>\n            </div>\n            <div>\n                <h4>ğŸµ Signal Processing</h4>\n                <p><strong>Phase analysis:</strong> Correlation between signal phases</p>\n                <p><strong>Circular convolution:</strong> Periodic signal filtering</p>\n            </div>\n        </div>\n    </div>\n</div>","title":"Circular Statistics: The Hidden Superpower"},{"content":"<div class=\"slide\">\n    <h1>Discrete Calculus on Circles: Data-Driven Operations</h1>\n    \n    <div class=\"info-box\">\n        <h3>ğŸ“Š Real-World Data = Discrete Samples</h3>\n        <p>In practice, we don't have continuous functionsâ€”we have <strong>time series data</strong> of circular quantities. How do we compute derivatives and integrals when our data wraps around?</p>\n    </div>\n    \n    <div class=\"two-column-equal\">\n        <div class=\"card\">\n            <h3>ğŸ“ˆ Discrete Derivatives (Angular Velocity)</h3>\n            <div class=\"code-example\">\n                <pre><code class=\"language-python\"># WRONG: Naive difference breaks at wraparound\nangles = [358Â°, 359Â°, 1Â°, 2Â°]  # Smooth rotation\nnaive_diff = [1Â°, 2Â°, -357Â°]  # HUGE SPIKE! ğŸ’¥\n\n# RIGHT: Vector space derivative\nvectors = [(cos(Î¸), sin(Î¸)) for Î¸ in angles]\ndvdt = [(v[i+1] - v[i])/dt for i in range(len(v)-1)]\n\n# Angular velocity from vector derivative\nÏ‰ = cross_product(v[i], dvdt[i]) / |v[i]|Â²\n# Result: [1Â°/s, 1Â°/s, 1Â°/s] âœ…</code></pre>\n            </div>\n        </div>\n        \n        <div class=\"card\">\n            <h3>ğŸ“‰ Discrete Integrals (Accumulated Rotation)</h3>\n            <div class=\"code-example\">\n                <pre><code class=\"language-python\"># WRONG: Sum angles directly\nangular_velocities = [90Â°/s, 90Â°/s, 90Â°/s, 90Â°/s]\nnaive_sum = sum(angular_velocities) * dt  # 360Â°???\n\n# RIGHT: Accumulate in vector space\nv_accumulated = [1, 0]  # Start at 0Â°\nfor Ï‰ in angular_velocities:\n    # Rotate vector by angular increment\n    dÎ¸ = Ï‰ * dt\n    rotation = [[cos(dÎ¸), -sin(dÎ¸)],\n                [sin(dÎ¸), cos(dÎ¸)]]\n    v_accumulated = rotation @ v_accumulated\n\nfinal_angle = atan2(v_accumulated)  # Correct!</code></pre>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"success-box\">\n        <h3>ğŸ”§ Practical Applications in Data Science</h3>\n        <div class=\"two-column\">\n            <div>\n                <h4>ğŸŒŠ Wind Speed Analysis</h4>\n                <div class=\"code-example\">\n                    <pre><code class=\"language-python\"># Wind direction time series (sampled every hour)\nwind_dirs = [350Â°, 355Â°, 0Â°, 5Â°, 10Â°]  # North-ish\nwind_speeds = [10, 12, 15, 14, 11]  # mph\n\n# Compute wind vector components\nu = [-speed * sin(dir) for speed, dir in zip(wind_speeds, wind_dirs)]\nv = [-speed * cos(dir) for speed, dir in zip(wind_speeds, wind_dirs)]\n\n# Now can compute proper derivatives!\ndu_dt = np.gradient(u)  # Wind acceleration (east)\ndv_dt = np.gradient(v)  # Wind acceleration (north)</code></pre>\n                </div>\n            </div>\n            <div>\n                <h4>ğŸ¤– Robot Heading Tracking</h4>\n                <div class=\"code-example\">\n                    <pre><code class=\"language-python\"># IMU gives heading samples at 100Hz\nheadings = sensor.get_heading_buffer()  # [Î¸â‚, Î¸â‚‚, ..., Î¸â‚™]\ndt = 0.01  # 10ms between samples\n\n# Convert to unit vectors\nvectors = [(cos(h), sin(h)) for h in headings]\n\n# Compute angular velocity using discrete derivative\nangular_vel = []\nfor i in range(len(vectors)-1):\n    # Cross product gives rotation rate\n    Ï‰ = (vectors[i][0]*vectors[i+1][1] - \n         vectors[i][1]*vectors[i+1][0]) / dt\n    angular_vel.append(Ï‰)\n\n# Smooth using windowed average (no wraparound issues!)\nsmoothed_Ï‰ = moving_average(angular_vel, window=10)</code></pre>\n                </div>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"warning-box\">\n        <h3>âš ï¸ Common Pitfalls with Discrete Circular Data</h3>\n        <div class=\"three-column\">\n            <div>\n                <h4>âŒ Phase Unwrapping</h4>\n                <p><code>unwrap([350Â°, 10Â°])</code> â†’ <code>[350Â°, 370Â°]</code></p>\n                <p>Creates artificial trends! Use vectors instead.</p>\n            </div>\n            <div>\n                <h4>âŒ Modulo Arithmetic</h4>\n                <p><code>(Î¸â‚‚ - Î¸â‚) % 360</code></p>\n                <p>Loses direction info. Vector difference preserves it.</p>\n            </div>\n            <div>\n                <h4>âŒ Averaging Raw Angles</h4>\n                <p><code>mean([0Â°, 90Â°, 180Â°, 270Â°])</code> â†’ <code>135Â°</code></p>\n                <p>Should be undefined! Vector mean â†’ 0.</p>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"highlight-box\">\n        <h3>ğŸ¯ The Key Pattern for Discrete Operations</h3>\n        <div class=\"formula-showcase\">\n            <div style=\"text-align: center; padding: 20px;\">\n                <p style=\"font-size: 18px; margin: 10px 0;\">\n                    <strong>Discrete Samples</strong> â†’ <strong>Vector Time Series</strong> â†’ <strong>Standard Numpy Operations</strong> â†’ <strong>Interpret Results</strong>\n                </p>\n                <p style=\"margin-top: 20px;\">\n                    <code>Î¸[t]</code> â†’ <code>[cos(Î¸[t]), sin(Î¸[t])]</code> â†’ <code>np.gradient(), np.cumsum()</code> â†’ <code>atan2() if needed</code>\n                </p>\n            </div>\n        </div>\n        <p><strong>Bottom Line:</strong> NumPy/Pandas operations \"just work\" on vector representationsâ€”no special circular functions needed!</p>\n    </div>\n</div>","title":"Discrete Calculus on Circles: Data-Driven Operations"},{"content":"<div class=\"slide\">\n    <h1>Hidden Rings Everywhere: Beyond Angles & Time</h1>\n    \n    <div class=\"warning-box\">\n        <h3>ğŸ” Circular Quantities Hide in Plain Sight</h3>\n        <p>It's not just angles, time, and colors that wrap around. Here are the sneaky cyclic variables that cause bugs when you least expect them:</p>\n    </div>\n    \n    <div class=\"pattern-table\">\n        <table style=\"width: 100%; border-collapse: collapse;\">\n            <thead>\n                <tr style=\"background: rgba(33, 150, 243, 0.1); border-bottom: 2px solid #2196F3;\">\n                    <th style=\"padding: 12px; text-align: left; width: 30%;\">Domain</th>\n                    <th style=\"padding: 12px; text-align: left; width: 35%;\">âŒ Where Naive Math Fails</th>\n                    <th style=\"padding: 12px; text-align: left; width: 35%;\">âœ… Correct Embedding</th>\n                </tr>\n            </thead>\n            <tbody>\n                <tr style=\"border-bottom: 1px solid #444;\">\n                    <td style=\"padding: 10px;\"><strong>ğŸ“… Weekdays</strong><br><code>d âˆˆ {0..6}</code></td>\n                    <td style=\"padding: 10px;\">Mean of Sat(6) and Mon(1) = 3.5 (Wed)?<br><span style=\"color: #FF5722;\">Wrong! Should be Sunday</span></td>\n                    <td style=\"padding: 10px;\"><code>(cos(2Ï€d/7), sin(2Ï€d/7))</code></td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444; background: rgba(255, 255, 255, 0.02);\">\n                    <td style=\"padding: 10px;\"><strong>ğŸ”§ Rotary Encoders</strong><br><code>n âˆˆ [0, 4096)</code></td>\n                    <td style=\"padding: 10px;\">4095 â†’ 1 looks like âˆ’4094 ticks<br><span style=\"color: #FF5722;\">Actually just 2 ticks forward!</span></td>\n                    <td style=\"padding: 10px;\"><code>(cos(2Ï€n/N), sin(2Ï€n/N))</code></td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444;\">\n                    <td style=\"padding: 10px;\"><strong>ğŸŒ Longitude</strong><br><code>Î» âˆˆ [âˆ’180Â°, 180Â°)</code></td>\n                    <td style=\"padding: 10px;\">Mean of 179Â°E and 179Â°W = 0Â°?<br><span style=\"color: #FF5722;\">Points are 2Â° apart, not at Greenwich!</span></td>\n                    <td style=\"padding: 10px;\">3D vectors on sphere:<br><code>(cos(lat)cos(lon), cos(lat)sin(lon), sin(lat))</code></td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444; background: rgba(255, 255, 255, 0.02);\">\n                    <td style=\"padding: 10px;\"><strong>ğŸ“¡ Signal Phase</strong><br><code>Ï† âˆˆ [âˆ’Ï€, Ï€)</code></td>\n                    <td style=\"padding: 10px;\">Phase unwrapping creates artificial jumps<br><span style=\"color: #FF5722;\">Destroys true periodicity</span></td>\n                    <td style=\"padding: 10px;\">Complex: <code>e<sup>iÏ†</sup> = cos(Ï†) + iÂ·sin(Ï†)</code></td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444;\">\n                    <td style=\"padding: 10px;\"><strong>ğŸ’ Ring Buffers</strong><br><code>idx âˆˆ [0, size)</code></td>\n                    <td style=\"padding: 10px;\"><code>tail - head</code> wrong when wrapped<br><span style=\"color: #FF5722;\">Buffer looks full when nearly empty!</span></td>\n                    <td style=\"padding: 10px;\"><code>(idx + size) % size</code><br>or embed as angle</td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444; background: rgba(255, 255, 255, 0.02);\">\n                    <td style=\"padding: 10px;\"><strong>âš™ï¸ Gear Teeth</strong><br><code>tooth âˆˆ [0, N)</code></td>\n                    <td style=\"padding: 10px;\">Tooth 0 and Tooth N-1 look maximally apart<br><span style=\"color: #FF5722;\">They're actually neighbors!</span></td>\n                    <td style=\"padding: 10px;\"><code>(cos(2Ï€t/N), sin(2Ï€t/N))</code></td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444;\">\n                    <td style=\"padding: 10px;\"><strong>ğŸ§¬ Circadian Phase</strong><br><code>t âˆˆ [0, 24h)</code></td>\n                    <td style=\"padding: 10px;\">Sleep at 23:00 and 01:00 averages to noon?<br><span style=\"color: #FF5722;\">Should be midnight!</span></td>\n                    <td style=\"padding: 10px;\"><code>(cos(2Ï€t/24), sin(2Ï€t/24))</code></td>\n                </tr>\n                <tr style=\"border-bottom: 1px solid #444; background: rgba(255, 255, 255, 0.02);\">\n                    <td style=\"padding: 10px;\"><strong>ğŸ“Š Months of Year</strong><br><code>m âˆˆ {1..12}</code></td>\n                    <td style=\"padding: 10px;\">Dec(12) to Jan(1) = -11 months?<br><span style=\"color: #FF5722;\">Just 1 month forward!</span></td>\n                    <td style=\"padding: 10px;\"><code>(cos(2Ï€m/12), sin(2Ï€m/12))</code></td>\n                </tr>\n            </tbody>\n        </table>\n    </div>\n    \n    <div class=\"highlight-box\">\n        <h3>ğŸ’¡ The Universal Solution</h3>\n        <div class=\"formula-showcase\">\n            <p style=\"text-align: center; font-size: 18px; margin: 15px 0;\">\n                <strong>For ANY cyclic quantity with period T starting at offset s:</strong>\n            </p>\n            <p style=\"text-align: center; font-size: 20px; background: rgba(76, 175, 80, 0.1); padding: 15px; border-radius: 8px;\">\n                <code>Î¸ = 2Ï€(value - s)/T</code> â†’ <code>(cos(Î¸), sin(Î¸))</code>\n            </p>\n            <p style=\"text-align: center; margin-top: 15px;\">\n                Do all your math in vector space, then project back if needed!\n            </p>\n        </div>\n    </div>\n    \n    <div class=\"info-box\">\n        <h3>ğŸ¯ When to Suspect a Hidden Ring</h3>\n        <div class=\"two-column\">\n            <div>\n                <h4>ğŸš© Red Flags in Your Data</h4>\n                <ul>\n                    <li>Values that \"roll over\" or \"wrap\"</li>\n                    <li>Modulo operations in the code</li>\n                    <li>Special cases for \"crossing midnight\"</li>\n                    <li>Discontinuous jumps in time series</li>\n                    <li>\"Distance\" that depends on direction</li>\n                </ul>\n            </div>\n            <div>\n                <h4>ğŸ› Classic Bug Patterns</h4>\n                <ul>\n                    <li>Averages that are wildly wrong</li>\n                    <li>Interpolation that takes the \"long way\"</li>\n                    <li>Derivatives with impossible spikes</li>\n                    <li>Sorting that puts neighbors far apart</li>\n                    <li>Clustering that splits natural groups</li>\n                </ul>\n            </div>\n        </div>\n    </div>\n</div>","title":"Hidden Rings Everywhere: Beyond Angles & Time"},{"content":"<div class=\"slide\">\n    <h1>When Zero Isn't Special: Arbitrary Wrap Points</h1>\n    \n    <div class=\"warning-box\">\n        <h3>ğŸ¯ Real Hardware Doesn't Care About Zero</h3>\n        <p>Many circular quantities in the wild wrap at arbitrary values, not nice round numbers. The math still worksâ€”you just need to normalize first!</p>\n    </div>\n    \n    <div class=\"two-column-equal\">\n        <div class=\"card\">\n            <h3>ğŸ“Š Common Non-Zero Wrap Domains</h3>\n            <div class=\"example-list\">\n                <div class=\"example-item\">\n                    <h4>ğŸ§­ Compass: [-180Â°, +180Â°)</h4>\n                    <p>Wraps at Â±180Â°, not 0Â°/360Â°</p>\n                    <code>bearing âˆˆ [-180, 180)</code>\n                </div>\n                <div class=\"example-item\">\n                    <h4>ğŸ”§ Rotary Encoder: [1000, 5096)</h4>\n                    <p>12-bit encoder with arbitrary offset</p>\n                    <code>counts âˆˆ [1000, 5096)</code>\n                </div>\n                <div class=\"example-item\">\n                    <h4>ğŸ“¡ Phase: [-Ï€, +Ï€)</h4>\n                    <p>Centered around zero, not starting at it</p>\n                    <code>phase âˆˆ [-Ï€, Ï€)</code>\n                </div>\n                <div class=\"example-item\">\n                    <h4>ğŸ—“ï¸ Fiscal Year: [Apr, Mar]</h4>\n                    <p>Wraps at March/April boundary</p>\n                    <code>month âˆˆ [4, 3] (mod 12)</code>\n                </div>\n                <div class=\"example-item\">\n                    <h4>ğŸŒ¡ï¸ ADC Values: [512, 3584)</h4>\n                    <p>Sensor with DC offset and limited range</p>\n                    <code>adc âˆˆ [512, 3584)</code>\n                </div>\n            </div>\n        </div>\n        \n        <div class=\"card\">\n            <h3>âœ¨ The Universal Transform</h3>\n            <div class=\"formula-showcase\" style=\"background: rgba(76, 175, 80, 0.1); padding: 20px; border-radius: 8px; margin: 20px 0;\">\n                <h4>For any circular quantity:</h4>\n                <p><code>value âˆˆ [min, max)</code></p>\n                <p style=\"margin: 15px 0;\">â†“</p>\n                <p><strong>1. Normalize to [0, 1):</strong></p>\n                <p><code>t = (value - min) / (max - min)</code></p>\n                <p style=\"margin: 15px 0;\">â†“</p>\n                <p><strong>2. Embed on unit circle:</strong></p>\n                <p><code>v = (cos(2Ï€t), sin(2Ï€t))</code></p>\n                <p style=\"margin: 15px 0;\">â†“</p>\n                <p><strong>3. Do your math in â„Â²</strong></p>\n                <p style=\"margin: 15px 0;\">â†“</p>\n                <p><strong>4. Project back if needed:</strong></p>\n                <p><code>result = min + (max-min) Ã— atan2(v.y, v.x)/(2Ï€)</code></p>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"success-box\">\n        <h3>ğŸ’» Code Example: Averaging Compass Bearings</h3>\n        <div class=\"code-example\">\n            <pre><code class=\"language-python\"># PROBLEM: Compass uses [-180Â°, +180Â°) not [0Â°, 360Â°)\nbearings = [170, 175, -175, -170]  # All pointing roughly south\n\n# WRONG: Simple average\nnaive_mean = np.mean(bearings)  # = 0Â° (North!) ğŸ¤¦\n\n# RIGHT: Normalize â†’ Embed â†’ Average â†’ Denormalize\ndef circular_mean(values, min_val, max_val):\n    # Step 1: Normalize to [0, 1)\n    period = max_val - min_val\n    normalized = [(v - min_val) / period for v in values]\n    \n    # Step 2: Embed on circle\n    vectors = [(np.cos(2*np.pi*t), np.sin(2*np.pi*t)) for t in normalized]\n    \n    # Step 3: Average in vector space\n    mean_vector = np.mean(vectors, axis=0)\n    \n    # Step 4: Project back to original domain\n    angle = np.atan2(mean_vector[1], mean_vector[0])\n    result = min_val + (angle / (2*np.pi)) * period\n    if result < min_val:\n        result += period\n    return result\n\ncorrect_mean = circular_mean(bearings, -180, 180)  # â‰ˆ 175Â° South! âœ…</code></pre>\n        </div>\n    </div>\n    \n    <div class=\"info-box\">\n        <h3>ğŸ”§ Practical Examples</h3>\n        <div class=\"three-column\">\n            <div>\n                <h4>ğŸ® Game Development</h4>\n                <div class=\"code-snippet\">\n                    <pre><code># Joystick angle âˆˆ [-Ï€, Ï€]\njs_angle = atan2(js.y, js.x)\n# Don't compare directly!\n# Normalize first</code></pre>\n                </div>\n            </div>\n            <div>\n                <h4>ğŸ¤– Robotics</h4>\n                <div class=\"code-snippet\">\n                    <pre><code># Encoder wraps at 8192\n# but starts at 1000\nactual_pos = 1000 + \n  (raw - 1000) % 7192</code></pre>\n                </div>\n            </div>\n            <div>\n                <h4>ğŸ“Š Time Series</h4>\n                <div class=\"code-snippet\">\n                    <pre><code># Fiscal quarters Q2-Q1\n# April = month 0\nfiscal_month = \n  (cal_month - 4) % 12</code></pre>\n                </div>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"highlight-box\">\n        <h3>ğŸ¯ Key Insight</h3>\n        <p style=\"font-size: 18px; text-align: center; margin: 20px 0;\">\n            <strong>The \"zero point\" is arbitrary!</strong> What matters is the <em>topology</em>â€”that values wrap around.\n        </p>\n        <p style=\"text-align: center;\">\n            Always normalize to [0, 1) or [0, 2Ï€) before embedding. Your vector math doesn't care where the original wrap point was.\n        </p>\n    </div>\n</div>","title":"When Zero Isn't Special: Arbitrary Wrap Points"},{"content":"<div class=\"slide\">\n    <h1>Real Example: Robot/Drone Heading Control</h1>\n    \n    <div class=\"problem-setup\">\n        <h3>ğŸ¤– The Scenario</h3>\n        <p>PID controller needs to turn robot from 350Â° to 10Â°</p>\n    </div>\n    \n    <div class=\"visual-comparison\">\n        <div class=\"approach bad-approach\">\n            <h4>âŒ Naive PID Error</h4>\n            <svg width=\"500\" height=\"500\" viewBox=\"0 0 250 250\">\n                <circle cx=\"125\" cy=\"125\" r=\"100\" fill=\"none\" stroke=\"#333\" stroke-width=\"2\"/>\n                \n                <!-- Current heading: 350Â° -->\n                <line x1=\"125\" y1=\"125\" x2=\"115\" y2=\"25\" stroke=\"#2196F3\" stroke-width=\"4\"/>\n                <circle cx=\"115\" cy=\"25\" r=\"6\" fill=\"#2196F3\"/>\n                <text x=\"75\" y=\"15\" font-size=\"12\" fill=\"#FFC107\">Current: 350Â°</text>\n                \n                <!-- Target heading: 10Â° -->\n                <line x1=\"125\" y1=\"125\" x2=\"135\" y2=\"25\" stroke=\"#4CAF50\" stroke-width=\"4\"/>\n                <circle cx=\"135\" cy=\"25\" r=\"6\" fill=\"#4CAF50\"/>\n                <text x=\"175\" y=\"15\" font-size=\"12\" fill=\"#FFC107\">Target: 10Â°</text>\n                \n                <!-- Wrong rotation path -->\n                <path d=\"M 115 25 A 100 100 0 1 1 135 25\" fill=\"none\" stroke=\"#FF5722\" stroke-width=\"3\" stroke-dasharray=\"5,5\"/>\n                <text x=\"125\" y=\"200\" text-anchor=\"middle\" font-size=\"14\" fill=\"#FF5722\" font-weight=\"bold\">\n                    Error: -340Â°\n                </text>\n                <text x=\"125\" y=\"220\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FF5722\">\n                    Spins backward!\n                </text>\n            </svg>\n            \n            <pre><code>error = target - current\nerror = 10 - 350 = -340Â°\n# Robot spins almost full circle\n# the wrong way!</code></pre>\n        </div>\n        \n        <div class=\"approach good-approach\">\n            <h4>âœ… Embedded Solution</h4>\n            <svg width=\"500\" height=\"500\" viewBox=\"0 0 250 250\">\n                <circle cx=\"125\" cy=\"125\" r=\"100\" fill=\"none\" stroke=\"#333\" stroke-width=\"2\"/>\n                \n                <!-- Current heading: 350Â° -->\n                <line x1=\"125\" y1=\"125\" x2=\"115\" y2=\"25\" stroke=\"#2196F3\" stroke-width=\"4\"/>\n                <circle cx=\"115\" cy=\"25\" r=\"6\" fill=\"#2196F3\"/>\n                <text x=\"75\" y=\"15\" font-size=\"12\" fill=\"#FFC107\">Current: 350Â°</text>\n                \n                <!-- Target heading: 10Â° -->\n                <line x1=\"125\" y1=\"125\" x2=\"135\" y2=\"25\" stroke=\"#4CAF50\" stroke-width=\"4\"/>\n                <circle cx=\"135\" cy=\"25\" r=\"6\" fill=\"#4CAF50\"/>\n                <text x=\"175\" y=\"15\" font-size=\"12\" fill=\"#FFC107\">Target: 10Â°</text>\n                \n                <!-- Correct rotation path -->\n                <path d=\"M 115 25 A 100 100 0 0 1 135 25\" fill=\"none\" stroke=\"#4CAF50\" stroke-width=\"3\"/>\n                <text x=\"125\" y=\"200\" text-anchor=\"middle\" font-size=\"14\" fill=\"#4CAF50\" font-weight=\"bold\">\n                    Error: +20Â°\n                </text>\n                <text x=\"125\" y=\"220\" text-anchor=\"middle\" font-size=\"12\" fill=\"#4CAF50\">\n                    Efficient turn!\n                </text>\n            </svg>\n            \n            <pre><code>def heading_error(current, target):\n    c_vec = [cos(current), sin(current)]\n    t_vec = [cos(target), sin(target)]\n    return atan2(cross(c_vec, t_vec), \n                 dot(c_vec, t_vec))\n# Returns: +20Â° (shortest path!)</code></pre>\n        </div>\n    </div>\n    \n    <div class=\"implementation\">\n        <h3>Complete PID Controller Fix</h3>\n        <pre><code class=\"language-python\">class HeadingController:\n    def __init__(self, kp=1.0, ki=0.1, kd=0.05):\n        self.kp, self.ki, self.kd = kp, ki, kd\n        self.integral = 0\n        self.last_error = 0\n        \n    def compute_control(self, current_heading, target_heading, dt):\n        # Embed both headings as vectors\n        current_vec = np.array([np.cos(current_heading), np.sin(current_heading)])\n        target_vec = np.array([np.cos(target_heading), np.sin(target_heading)])\n        \n        # Compute signed angle error (always shortest path)\n        error = np.arctan2(\n            current_vec[0] * target_vec[1] - current_vec[1] * target_vec[0],\n            current_vec[0] * target_vec[0] + current_vec[1] * target_vec[1]\n        )\n        \n        # Standard PID computation (now with correct error!)\n        self.integral += error * dt\n        derivative = (error - self.last_error) / dt\n        \n        control = self.kp * error + self.ki * self.integral + self.kd * derivative\n        self.last_error = error\n        \n        return control  # Angular velocity command</code></pre>\n    </div>\n    \n    <div class=\"impact-note\">\n        <p>ğŸ’° <strong>Real Impact:</strong> Shipping routes crossing the Pacific get drawn spanning ~359Â° longitude instead of the short ~20 nautical mile path due to antimeridian wraparound bugs in mapping SDKs</p>\n    </div>\n</div>\n\n","title":"Real Example: Robot/Drone Heading Control"},{"content":"<div class=\"slide\">\n    <h1>Color Interpolation (HSV Hue)</h1>\n    \n    <div class=\"color-problem\">\n        <h3>ğŸ¨ The Problem: Fade from Red to Magenta</h3>\n        <div class=\"color-display\">\n            <div class=\"color-box\" style=\"background: hsl(10, 100%, 50%);\">\n                <span>Red<br>10Â°</span>\n            </div>\n            <div class=\"arrow\">â†’</div>\n            <div class=\"color-box\" style=\"background: hsl(350, 100%, 50%);\">\n                <span>Magenta<br>350Â°</span>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"approaches\">\n        <div class=\"approach-bad\">\n            <h4>âŒ Linear Interpolation</h4>\n            <div class=\"color-gradient bad-gradient\">\n                <div class=\"color-step\" style=\"background: hsl(10, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(68, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(126, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(180, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(238, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(296, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(350, 100%, 50%);\"></div>\n            </div>\n            <pre><code>mid_hue = (10 + 350) / 2 = 180Â°  # CYAN!?\n# Goes through entire rainbow!</code></pre>\n        </div>\n        \n        <div class=\"approach-good\">\n            <h4>âœ… Circular Interpolation</h4>\n            <div class=\"color-gradient good-gradient\">\n                <div class=\"color-step\" style=\"background: hsl(10, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(6, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(2, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(358, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(354, 100%, 50%);\"></div>\n                <div class=\"color-step\" style=\"background: hsl(350, 100%, 50%);\"></div>\n            </div>\n            <pre><code>vec1 = [cos(10Â°), sin(10Â°)]\nvec2 = [cos(350Â°), sin(350Â°)]\nvec_mid = normalize((vec1 + vec2) / 2)\nmid_hue = atan2(vec_mid)  # 0Â° (stays red!)</code></pre>\n        </div>\n    </div>\n    \n    <div class=\"implementation-example\">\n        <h3>Production-Ready Color Lerp</h3>\n        <pre><code class=\"javascript\">function lerpHue(h1, h2, t) {\n    // Convert hues to radians\n    const theta1 = h1 * Math.PI / 180;\n    const theta2 = h2 * Math.PI / 180;\n    \n    // Embed on unit circle\n    const v1 = [Math.cos(theta1), Math.sin(theta1)];\n    const v2 = [Math.cos(theta2), Math.sin(theta2)];\n    \n    // Interpolate vectors\n    const vt = [\n        (1 - t) * v1[0] + t * v2[0],\n        (1 - t) * v1[1] + t * v2[1]\n    ];\n    \n    // Normalize and convert back\n    const norm = Math.sqrt(vt[0]**2 + vt[1]**2);\n    const thetaT = Math.atan2(vt[1]/norm, vt[0]/norm);\n    \n    return (thetaT * 180 / Math.PI + 360) % 360;\n}\n\n// CSS gradient generator\nfunction generateGradient(h1, h2, steps) {\n    const colors = [];\n    for (let i = 0; i <= steps; i++) {\n        const t = i / steps;\n        const h = lerpHue(h1, h2, t);\n        colors.push(`hsl(${h}, 100%, 50%)`);\n    }\n    return `linear-gradient(90deg, ${colors.join(', ')})`;\n}</code></pre>\n    </div>\n    \n    <div class=\"use-cases\">\n        <h3>Where This Matters</h3>\n        <ul>\n            <li>ğŸ® <strong>Game UI:</strong> Health bar color transitions</li>\n            <li>ğŸ“Š <strong>Data Viz:</strong> Temperature/pressure heatmaps</li>\n            <li>ğŸ¬ <strong>Animation:</strong> Smooth color transitions</li>\n            <li>ğŸ–¼ï¸ <strong>Image Processing:</strong> Color correction algorithms</li>\n        </ul>\n    </div>\n</div>\n\n","title":"Color Interpolation (HSV Hue)"},{"content":"<div class=\"slide\">\n    <h1>The General Pattern</h1>\n    \n    <div class=\"pattern-table\">\n        <h3>Any Circular Quantity Can Be Embedded!</h3>\n        <table>\n            <thead>\n                <tr>\n                    <th>Wraparound Quantity</th>\n                    <th>Traditional Problems</th>\n                    <th>Embedding Solution</th>\n                    <th>Benefits</th>\n                </tr>\n            </thead>\n            <tbody>\n                <tr>\n                    <td><strong>Angle</strong><br>Î¸ âˆˆ [0, 2Ï€)</td>\n                    <td>Gimbal lock, discontinuities</td>\n                    <td>(cos Î¸, sin Î¸) âˆˆ â„Â²</td>\n                    <td>Smooth interpolation</td>\n                </tr>\n                <tr>\n                    <td><strong>Time of Day</strong><br>t âˆˆ [0, 24)</td>\n                    <td>Midnight wraparound</td>\n                    <td>(cos 2Ï€t/24, sin 2Ï€t/24)</td>\n                    <td>No special cases</td>\n                </tr>\n                <tr>\n                    <td><strong>Day of Week</strong><br>d âˆˆ {0..6}</td>\n                    <td>Weekend averaging</td>\n                    <td>e^(2Ï€id/7) or 7D one-hot</td>\n                    <td>Correct statistics</td>\n                </tr>\n                <tr>\n                    <td><strong>Phase</strong><br>Ï† âˆˆ [-Ï€, Ï€)</td>\n                    <td>Phase unwrapping</td>\n                    <td>e^(iÏ†) âˆˆ â„‚</td>\n                    <td>Natural operations</td>\n                </tr>\n                <tr>\n                    <td><strong>Encoder Position</strong><br>n âˆˆ [0, 4096)</td>\n                    <td>Rollover detection</td>\n                    <td>(cos 2Ï€n/4096, sin 2Ï€n/4096)</td>\n                    <td>Continuous tracking</td>\n                </tr>\n            </tbody>\n        </table>\n    </div>\n    \n    <div class=\"universal-class\">\n        <h3>One Class to Rule Them All</h3>\n        <pre><code class=\"language-python\">class CircularQuantity:\n    \"\"\"Universal handler for any quantity that wraps around\"\"\"\n    \n    def __init__(self, value, period):\n        self.period = period\n        self.value = value % period  # Normalize to [0, period)\n        \n        # THE KEY: Embed on unit circle\n        theta = 2 * np.pi * self.value / period\n        self.vec = np.array([np.cos(theta), np.sin(theta)])\n    \n    def shortest_distance_to(self, other):\n        \"\"\"Always returns shortest signed distance\"\"\"\n        angle = np.arctan2(\n            self.vec[0] * other.vec[1] - self.vec[1] * other.vec[0],\n            self.vec[0] * other.vec[0] + self.vec[1] * other.vec[1]\n        )\n        return angle * self.period / (2 * np.pi)\n    \n    def average_with(self, others):\n        \"\"\"Proper circular mean\"\"\"\n        all_vecs = [self.vec] + [o.vec for o in others]\n        mean_vec = np.mean(all_vecs, axis=0)\n        mean_vec /= np.linalg.norm(mean_vec)  # Renormalize\n        \n        angle = np.arctan2(mean_vec[1], mean_vec[0])\n        mean_value = angle * self.period / (2 * np.pi)\n        return CircularQuantity(mean_value, self.period)\n    \n    def interpolate_to(self, other, t):\n        \"\"\"Smooth interpolation along shortest path\"\"\"\n        # SLERP for perfect circular interpolation\n        dot = np.dot(self.vec, other.vec)\n        omega = np.arccos(np.clip(dot, -1, 1))\n        \n        if abs(omega) < 1e-10:  # Vectors are identical\n            return self\n        \n        vec_t = (np.sin((1-t)*omega)/np.sin(omega) * self.vec +\n                 np.sin(t*omega)/np.sin(omega) * other.vec)\n        \n        angle = np.arctan2(vec_t[1], vec_t[0])\n        value = angle * self.period / (2 * np.pi)\n        return CircularQuantity(value, self.period)</code></pre>\n    </div>\n    \n    <div class=\"examples-grid\">\n        <div class=\"example\">\n            <h4>ğŸ“ Angles</h4>\n            <pre><code>a1 = CircularQuantity(350, 360)\na2 = CircularQuantity(10, 360)\ndist = a1.shortest_distance_to(a2)\n# Result: 20Â°</code></pre>\n        </div>\n        \n        <div class=\"example\">\n            <h4>â° Time</h4>\n            <pre><code>t1 = CircularQuantity(23.5, 24)\nt2 = CircularQuantity(0.5, 24)\navg = t1.average_with([t2])\n# Result: 0.0 (midnight)</code></pre>\n        </div>\n        \n        <div class=\"example\">\n            <h4>ğŸ“… Weekdays</h4>\n            <pre><code>days = [CircularQuantity(6, 7),  # Sat\n        CircularQuantity(0, 7),  # Sun\n        CircularQuantity(1, 7)]  # Mon\navg = days[0].average_with(days[1:])\n# Result: 0 (Sunday)</code></pre>\n        </div>\n        \n        <div class=\"example\">\n            <h4>ğŸ”„ Encoder</h4>\n            <pre><code>e1 = CircularQuantity(4090, 4096)\ne2 = CircularQuantity(10, 4096)\ndelta = e1.shortest_distance_to(e2)\n# Result: 20 counts</code></pre>\n        </div>\n    </div>\n</div>\n\n","title":"The General Pattern"},{"content":"<div class=\"slide\">\n    <h1>The Mathematical Insight</h1>\n    \n    <div class=\"info-box\">\n        <h3>Why Does This Work?</h3>\n        <div class=\"visual-demo\">\n            <svg width=\"600\" height=\"150\" viewBox=\"0 0 600 150\">\n                <!-- Line with endpoints -->\n                <g transform=\"translate(100, 75)\">\n                    <line x1=\"-60\" y1=\"0\" x2=\"60\" y2=\"0\" stroke=\"#333\" stroke-width=\"3\"/>\n                    <circle cx=\"-60\" cy=\"0\" r=\"6\" fill=\"#FF5722\"/>\n                    <circle cx=\"60\" cy=\"0\" r=\"6\" fill=\"#FF5722\"/>\n                    <text x=\"0\" y=\"30\" text-anchor=\"middle\" font-size=\"14\" fill=\"#FFC107\">Line has endpoints</text>\n                </g>\n                \n                <!-- vs -->\n                <text x=\"300\" y=\"80\" text-anchor=\"middle\" font-size=\"20\" fill=\"#FFC107\">vs</text>\n                \n                <!-- Circle -->\n                <g transform=\"translate(500, 40)\">\n                    <circle cx=\"0\" cy=\"0\" r=\"50\" fill=\"none\" stroke=\"#333\" stroke-width=\"3\"/>\n                    <text x=\"0\" y=\"70\" text-anchor=\"middle\" font-size=\"14\" fill=\"#FFC107\">Circle has none!</text>\n                </g>\n            </svg>\n        </div>\n        <p class=\"text-center\">A circle (SÂ¹) is topologically different from a line segment. Linear math breaks at the wraparound point.</p>\n    </div>\n    \n    <div class=\"two-column-equal\">\n        <div class=\"card\">\n            <h3>ğŸŒ The Circle Problem</h3>\n            <ul>\n                <li><strong>Circles have no \"beginning\" or \"end\"</strong></li>\n                <li>359Â° and 1Â° are neighbors</li>\n                <li>But arithmetic says 359 - 1 = 358 ğŸ¤¦</li>\n                <li>â†’ Solution: Use 2D vectors instead!</li>\n            </ul>\n        </div>\n        \n        <div class=\"card\">\n            <h3>ğŸ”„ The Wrapping Function</h3>\n            <svg width=\"300\" height=\"120\" viewBox=\"0 0 300 120\">\n                <path d=\"M 20 80 Q 70 20, 120 80 T 220 80 T 280 80\" stroke=\"#2196F3\" stroke-width=\"3\" fill=\"none\"/>\n                <text x=\"50\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">0Â°</text>\n                <text x=\"120\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">360Â°</text>\n                <text x=\"190\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">720Â°</text>\n                <text x=\"260\" y=\"60\" text-anchor=\"middle\" font-size=\"12\" fill=\"#FFC107\">1080Â°</text>\n            </svg>\n            <p>Angles wrap around: 370Â° = 10Â°, 730Â° = 10Â°, etc. The infinite line wraps onto the finite circle.</p>\n        </div>\n    </div>\n    \n    <div class=\"success-box\">\n        <h3>ğŸ”‘ The Universal Pattern</h3>\n        <div class=\"process-flow\">\n            <div class=\"process-step\">\n                <h4>Problem Space</h4>\n                <p>Non-linear manifold</p>\n                <small>(circle, sphere, torus)</small>\n            </div>\n            <div class=\"process-step\">\n                <h4>Embedding</h4>\n                <p>Lift to linear space</p>\n                <small>(â„Â², â„Â³, â„‚)</small>\n            </div>\n            <div class=\"process-step\">\n                <h4>Computation</h4>\n                <p>Do linear algebra</p>\n                <small>(add, average, interpolate)</small>\n            </div>\n            <div class=\"process-step\">\n                <h4>Projection</h4>\n                <p>Map back to manifold</p>\n                <small>(atan2, normalize)</small>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"two-column\">\n        <div class=\"warning-box\">\n            <h3>ğŸ¯ Where Else This Appears</h3>\n            <ul>\n                <li><strong>Quaternions:</strong> 3D rotations in 4D space</li>\n                <li><strong>Complex Numbers:</strong> 2D rotations in â„‚</li>\n                <li><strong>Fourier Transform:</strong> Signals on unit circle</li>\n                <li><strong>Neural Networks:</strong> Learned embeddings</li>\n            </ul>\n        </div>\n        \n        <div class=\"highlight-box\">\n            <h3>ğŸ’­ Mathematical Insight</h3>\n            <blockquote style=\"font-style: italic; margin: 10px 0;\">\n                \"The introduction of numbers as coordinates is an act of violence.\"\n                <br><cite style=\"font-size: 14px;\">â€” Hermann Weyl</cite>\n            </blockquote>\n            <p><strong>But sometimes, the right coordinates make the violence worthwhile!</strong></p>\n        </div>\n    </div>\n</div>\n\n","title":"The Mathematical Insight"},{"content":"<div class=\"slide\">\n    <h1>Key Takeaways</h1>\n    \n    <div class=\"main-takeaway\">\n        <h2>ğŸ¯ The Golden Rule</h2>\n        <div class=\"golden-rule\">\n            <p>When you see a quantity that wraps around,</p>\n            <p><strong>ask: \"Can I embed this on a circle?\"</strong></p>\n        </div>\n    </div>\n    \n    <div class=\"practical-steps\">\n        <h3>Your Debugging Checklist</h3>\n        <div class=\"checklist\">\n            <div class=\"check-item\">\n                <span class=\"checkbox\">âœ“</span>\n                <div>\n                    <h4>Spot the Wraparound</h4>\n                    <p>Does your value jump discontinuously? (359Â° â†’ 0Â°, 11:59 PM â†’ 12:00 AM)</p>\n                </div>\n            </div>\n            \n            <div class=\"check-item\">\n                <span class=\"checkbox\">âœ“</span>\n                <div>\n                    <h4>Identify the Period</h4>\n                    <p>What's the cycle length? (360Â° for angles, 24 hours for time, 7 for weekdays)</p>\n                </div>\n            </div>\n            \n            <div class=\"check-item\">\n                <span class=\"checkbox\">âœ“</span>\n                <div>\n                    <h4>Choose Your Embedding</h4>\n                    <p>Usually: <code style=\"display: inline-block;\">(cos(<span style=\"display: inline-block; vertical-align: middle;\"><span style=\"display: block; text-align: center; border-bottom: 1px solid;\">2Ï€x</span><span style=\"display: block; text-align: center;\">T</span></span>), sin(<span style=\"display: inline-block; vertical-align: middle;\"><span style=\"display: block; text-align: center; border-bottom: 1px solid;\">2Ï€x</span><span style=\"display: block; text-align: center;\">T</span></span>))</code> where <code>T</code> is the period</p>\n                </div>\n            </div>\n            \n            <div class=\"check-item\">\n                <span class=\"checkbox\">âœ“</span>\n                <div>\n                    <h4>Work in Vector Space</h4>\n                    <p>Do all math on the vectors, not the raw values</p>\n                </div>\n            </div>\n            \n            <div class=\"check-item\">\n                <span class=\"checkbox\">âœ“</span>\n                <div>\n                    <h4>Project Back When Needed</h4>\n                    <p>Use <code>atan2(y, x)</code> to recover the original quantity</p>\n                </div>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"libraries-section\">\n        <h3>Don't Reinvent: Use Libraries!</h3>\n        <div class=\"library-grid\">\n            <div class=\"library\">\n                <h4>Python</h4>\n                <ul>\n                    <li><code>scipy.stats.circmean</code></li>\n                    <li><code>astropy.coordinates</code></li>\n                    <li><code>pyquaternion</code></li>\n                </ul>\n            </div>\n            \n            <div class=\"library\">\n                <h4>JavaScript</h4>\n                <ul>\n                    <li><code>d3-interpolate</code></li>\n                    <li><code>three.js Quaternion</code></li>\n                    <li><code>tinycolor2</code></li>\n                </ul>\n            </div>\n            \n            <div class=\"library\">\n                <h4>C++</h4>\n                <ul>\n                    <li><code>Eigen::AngleAxis</code></li>\n                    <li><code>boost::geometry</code></li>\n                    <li><code>tf2</code> (ROS)</li>\n                </ul>\n            </div>\n            \n            <div class=\"library\">\n                <h4>MATLAB</h4>\n                <ul>\n                    <li><code>circstat toolbox</code></li>\n                    <li><code>Aerospace Toolbox</code></li>\n                    <li><code>angle()</code>, <code>unwrap()</code></li>\n                </ul>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"red-flags\">\n        <h3>ğŸš© Red Flags in Your Code</h3>\n        <div class=\"flag-list\">\n            <div class=\"flag\">\n                <code>if (angle > 180) angle -= 360;</code>\n                <span>â†’ Use circular math instead</span>\n            </div>\n            <div class=\"flag\">\n                <code>if (crossed_midnight) /* special case */</code>\n                <span>â†’ Embed on 24-hour circle</span>\n            </div>\n            <div class=\"flag\">\n                <code>while (phase > Ï€) phase -= 2Ï€;</code>\n                <span>â†’ Work with complex exponentials</span>\n            </div>\n            <div class=\"flag\">\n                <code>// TODO: handle wraparound</code>\n                <span>â†’ Today's the day!</span>\n            </div>\n        </div>\n    </div>\n    \n    <div class=\"success-story\">\n        <div class=\"story-content\">\n            <h3>ğŸ‰ Success Story</h3>\n            <p>\"After this talk, I fixed a 3-year-old GPS bearing bug in 5 lines of code. The robot finally drives in straight lines!\"</p>\n            <cite>â€” Future You</cite>\n        </div>\n    </div>\n</div>\n\n","title":"Key Takeaways"},{"content":"<div class=\"slide\">\n    <h1>Interactive Demo</h1>\n    \n    <div class=\"demo-container\">\n        <div class=\"demo-section\">\n            <h3>ğŸ® Try It Yourself: Angle Averaging</h3>\n            \n            <div class=\"angle-inputs\">\n                <label>\n                    Angle 1: <input type=\"range\" id=\"angle1\" min=\"0\" max=\"359\" value=\"350\">\n                    <span id=\"angle1-display\">350Â°</span>\n                </label>\n                <label>\n                    Angle 2: <input type=\"range\" id=\"angle2\" min=\"0\" max=\"359\" value=\"10\">\n                    <span id=\"angle2-display\">10Â°</span>\n                </label>\n            </div>\n            \n            <div class=\"demo-visual\">\n                <svg width=\"400\" height=\"400\" viewBox=\"0 0 400 400\" id=\"demo-svg\">\n                    <!-- Circle -->\n                    <circle cx=\"200\" cy=\"200\" r=\"150\" fill=\"none\" stroke=\"#333\" stroke-width=\"2\"/>\n                    \n                    <!-- Compass labels -->\n                    <text x=\"200\" y=\"30\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">0Â°</text>\n                    <text x=\"370\" y=\"205\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">90Â°</text>\n                    <text x=\"200\" y=\"380\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">180Â°</text>\n                    <text x=\"30\" y=\"205\" text-anchor=\"middle\" font-size=\"16\" font-weight=\"bold\" fill=\"#FFC107\">270Â°</text>\n                    \n                    <!-- Angle 1 line -->\n                    <line id=\"angle1-line\" x1=\"200\" y1=\"200\" x2=\"200\" y2=\"50\" stroke=\"#2196F3\" stroke-width=\"4\"/>\n                    <circle id=\"angle1-point\" cx=\"200\" cy=\"50\" r=\"8\" fill=\"#2196F3\"/>\n                    \n                    <!-- Angle 2 line -->\n                    <line id=\"angle2-line\" x1=\"200\" y1=\"200\" x2=\"200\" y2=\"50\" stroke=\"#4CAF50\" stroke-width=\"4\"/>\n                    <circle id=\"angle2-point\" cx=\"200\" cy=\"50\" r=\"8\" fill=\"#4CAF50\"/>\n                    \n                    <!-- Average (wrong) -->\n                    <line id=\"avg-wrong-line\" x1=\"200\" y1=\"200\" x2=\"200\" y2=\"350\" stroke=\"#FF5722\" stroke-width=\"3\" stroke-dasharray=\"5,5\" opacity=\"0\"/>\n                    <circle id=\"avg-wrong-point\" cx=\"200\" cy=\"350\" r=\"6\" fill=\"#FF5722\" opacity=\"0\"/>\n                    \n                    <!-- Average (correct) -->\n                    <line id=\"avg-correct-line\" x1=\"200\" y1=\"200\" x2=\"200\" y2=\"50\" stroke=\"#FFC107\" stroke-width=\"4\"/>\n                    <circle id=\"avg-correct-point\" cx=\"200\" cy=\"50\" r=\"8\" fill=\"#FFC107\"/>\n                </svg>\n            </div>\n            \n            <div class=\"demo-results\">\n                <div class=\"result wrong-result\">\n                    <h4>âŒ Linear Average</h4>\n                    <p id=\"wrong-result\">180Â°</p>\n                </div>\n                \n                <div class=\"result correct-result\">\n                    <h4>âœ… Circular Average</h4>\n                    <p id=\"correct-result\">0Â°</p>\n                </div>\n            </div>\n        </div>\n        \n        <div class=\"code-section\">\n            <h3>The Code Behind It</h3>\n            <pre><code id=\"demo-code\">function circularAverage(a1, a2) {\n    // Convert to radians\n    const theta1 = a1 * Math.PI / 180;\n    const theta2 = a2 * Math.PI / 180;\n    \n    // Embed on unit circle\n    const v1 = [Math.cos(theta1), Math.sin(theta1)];\n    const v2 = [Math.cos(theta2), Math.sin(theta2)];\n    \n    // Average vectors\n    const avgVec = [(v1[0] + v2[0])/2, (v1[1] + v2[1])/2];\n    \n    // Convert back to angle\n    const avgTheta = Math.atan2(avgVec[1], avgVec[0]);\n    return (avgTheta * 180 / Math.PI + 360) % 360;\n}</code></pre>\n        </div>\n    </div>\n</div>\n\n\n","title":"Interactive Demo"},{"content":"<div class=\"slide\" style=\"display:grid; gap:12px;\">\n  <h1>ğŸŒ GIS Demo: Longitude Wraparound at the International Date Line</h1>\n  \n  <div class=\"info-box\">\n    <h3>Real-World Problem: Computing Geographic Means</h3>\n    <p>When averaging GPS coordinates near Â±180Â° longitude (International Date Line), linear arithmetic fails spectacularly. A farm field split by the date line appears to span the entire globe!</p>\n  </div>\n  \n  <div id=\"map\" style=\"height:600px; border-radius:12px; overflow:hidden; border:1px solid rgba(255,255,255,.15)\"></div>\n  \n  <div style=\"display:flex; gap:16px; flex-wrap:wrap; align-items:center;\">\n    <label><input type=\"checkbox\" id=\"worldCopy\" checked> worldCopyJump (wrap world)</label>\n    <label><input type=\"checkbox\" id=\"noWrapTiles\"> tile noWrap (stop wrapping tiles)</label>\n    <span id=\"readout\" style=\"opacity:.9; font-family: monospace;\">Circular mean: â€¦</span>\n  </div>\n  \n  <div class=\"success-box\">\n    <h3>ğŸ¯ Try This: Drag the markers across the International Date Line!</h3>\n    <p><strong>Red marker</strong> = Wrong linear mean | <strong>Green marker</strong> = Correct circular mean</p>\n    <p>Watch how the linear mean jumps to the middle of the Atlantic while the circular mean stays put! This is the classic longitude wraparound bug.</p>\n  </div>\n  \n  <div class=\"code-example\">\n    <h3>Spherical Coordinate Embedding</h3>\n    <pre><code class=\"language-python\"># For full lat/long geometry operations\ndef latlon_to_cartesian(lat, lon):\n    \"\"\"Embed (lat,lon) in RÂ³ for linear operations\"\"\"\n    lat_rad = np.radians(lat)\n    lon_rad = np.radians(lon)\n    return np.array([\n        np.cos(lat_rad) * np.cos(lon_rad),  # x\n        np.cos(lat_rad) * np.sin(lon_rad),  # y  \n        np.sin(lat_rad)                     # z\n    ])\n\n# Geographic centroid (works across antimeridian!)\ndef geographic_centroid(lat_lon_pairs):\n    cartesian_points = [latlon_to_cartesian(lat, lon) \n                       for lat, lon in lat_lon_pairs]\n    mean_cartesian = np.mean(cartesian_points, axis=0)\n    mean_cartesian /= np.linalg.norm(mean_cartesian)  # Back to unit sphere\n    \n    # Convert back to lat/lon\n    lat = np.degrees(np.arcsin(mean_cartesian[2]))\n    lon = np.degrees(np.arctan2(mean_cartesian[1], mean_cartesian[0]))\n    return lat, lon</code></pre>\n  </div>\n  \n  <div class=\"warning-box\">\n    <h3>ğŸ¢ Company Applications</h3>\n    <ul>\n      <li><strong>Spatial Statistics:</strong> Computing true centroids for service areas</li>\n      <li><strong>Route Optimization:</strong> Distance calculations that work globally</li>\n      <li><strong>Geofencing:</strong> Boundary checks that don't break at meridians</li>\n      <li><strong>Data Quality:</strong> Detecting GPS outliers using proper spherical distance</li>\n    </ul>\n  </div>\n</div>\n\n","title":"ğŸŒ GIS Demo: Longitude Wraparound at the International Date Line"}];

let currentSlide = 0;

function showSlide(index) {
    if (index < 0 || index >= slidesData.length) return;
    
    const slideContent = document.getElementById('slide-content');
    slideContent.innerHTML = slidesData[index].content;
    
    // Update counter
    document.getElementById('current-slide').textContent = index + 1;
    document.getElementById('total-slides').textContent = slidesData.length;
    
    // Update navigation buttons
    const prevButton = document.querySelector('.nav-button');
    const nextButton = document.querySelector('.nav-button:last-child');
    
    prevButton.disabled = index === 0;
    nextButton.disabled = index === slidesData.length - 1;
    
    // Add fade in animation
    slideContent.style.animation = 'none';
    slideContent.offsetHeight; // Trigger reflow
    slideContent.style.animation = 'fadeIn 0.5s';
    
    // Re-run any scripts in the slide
    const scripts = slideContent.querySelectorAll('script');
    scripts.forEach(script => {
        try {
            const newScript = document.createElement('script');
            if (script.src) {
                newScript.src = script.src;
            } else {
                newScript.textContent = script.textContent;
            }
            script.parentNode.replaceChild(newScript, script);
        } catch (error) {
            console.warn('Error re-executing script:', error);
            // Try alternative approach - evaluate script directly
            try {
                eval(script.textContent);
            } catch (evalError) {
                console.error('Failed to execute script:', evalError);
            }
        }
    });
    
    currentSlide = index;
}

function nextSlide() {
    if (currentSlide < slidesData.length - 1) {
        currentSlide++;
        showSlide(currentSlide);
    }
}

function previousSlide() {
    if (currentSlide > 0) {
        currentSlide--;
        showSlide(currentSlide);
    }
}

function goToSlide(index) {
    if (index >= 0 && index < slidesData.length) {
        currentSlide = index;
        showSlide(currentSlide);
    }
}

// Keyboard navigation
document.addEventListener('keydown', function(event) {
    if (event.key === 'ArrowRight' || event.key === ' ') {
        event.preventDefault();
        nextSlide();
    } else if (event.key === 'ArrowLeft') {
        event.preventDefault();
        previousSlide();
    } else if (event.key >= '1' && event.key <= '9') {
        const slideNum = parseInt(event.key) - 1;
        if (slideNum < slidesData.length) {
            goToSlide(slideNum);
        }
    } else if (event.key === 'Home') {
        goToSlide(0);
    } else if (event.key === 'End') {
        goToSlide(slidesData.length - 1);
    }
});

